import { useState } from "react";

export const [user,setUser]=useSate();

export const begin=(hai)=>{
    let one=localStorage.getItem('who')
    if(one!=null)
    {
        user=hai;
    }
    localStorage.setItem('who',user)
    alert(user+" logged in");
}

export const end=()=>{
    localStorage.setItem('who','')
}
