import { useState } from "react";

let user="";

export const begin=(hai)=>{
    let one=localStorage.getItem('who')
    if(one!=null)
    {
        user=hai;
    }
    else{
        localStorage.setItem('who',hai)
    }
    alert(one)
}

export const end=()=>{
    localStorage.removeItem('who')
}
