import { useEffect } from "react"

const Wishlist=()=>{

    const person=localStorage.getItem('who')

    let simple=new Array()

    //const iterate=()=>{
        const txt=localStorage.getItem('wishlist')
        const tmp=JSON.parse(txt)
        simple=tmp.filter((data)=>{
            return data.user===person
        })
    //}
    
    //useEffect(iterate(),[])

    

    const boxstyle={
        minWidth:'300px',
        minHeight:'300px',
        marginRight:'10px'
    }

    return(
        <>
            <div className="row justify-content-center p-2">
            {
                        simple.map((data)=>(
                            <div className="card col-3 p-2 text-center" style={boxstyle}>
                                <img className="card-img" src={data.item.pic} alt="shoe"/>
                                <div className="card-body">
                                    <h1 className="card-title text-primary">{data.item.model}  {data.brand}</h1>
                                    <p className="card-text text-success">Colors {data.colors}</p>
                                    <p className="card-text text-warning">Type {data.type}</p>
                                    <p className="card-text text-danger">Price {data.cost}</p>
                                </div>
                            </div>
                        ))
                    }
            </div>
        </>
    )
}

export default Wishlist
