import { useState } from "react";

export let user="";

export const begin=(hai)=>{
    let one=localStorage.getItem('who')
    if(one!=null)
    {
        user=hai;
    }
    localStorage.setItem('who',user)
    alert(user)
}

export const end=()=>{
    localStorage.setItem('who','')
}
